import React, { useState } from 'react'
import { Search } from "@material-ui/icons"
import { useHistory } from "react-router";
import { useDispatch } from "react-redux";
import { Avatar } from '@material-ui/core';
import { ShoppingBasket } from '@material-ui/icons';
import { Person } from '@material-ui/icons';

function Header() {
    const history = useHistory();
    const [user , setUser] = useState(JSON.parse(localStorage.getItem('profile')));
    const [profileBar, setProfileBar] = useState(false);
    const [search, setSearch] = useState();
    const PF = "http://localhost:8800/images/";
    const dispatch = useDispatch();

    const logout = () => {
        dispatch({ type: 'LOGOUT' });
        history.push("/")
        setUser(null);
    }

    return (
        <div className="w-full fixed flex items-center z-50 justify-around shadow-md bg-white h-20">
            <div onClick={() => { history.push("/") }} className="font-mono cursor-pointer text-black">Final Proje</div>

            <div className="flex bg-gray-400 rounded-lg w-2/4 items-center ">
                <input className="flex bg-gray-200 h-10 rounded-l-lg  p-3 w-11/12 text-gray-900 items-center outline-none " value={search} onChange={e => { setSearch(e.target.value) }} placeholder="Aradığınız ürün, kategori veya markayı yazınız" type="text"></input>
                <Search />
            </div>
            {!user ? <div onClick={()=>{history.push("/giris")}} className="flex cursor-pointer text-black group"><p className="group-hover:text-yellow-600">Giriş Yap</p> <Person className="group-hover:text-yellow-600"/> </div> : <div className="flex w-40 items-center justify-between">  
               <div className="text-black cursor-pointer group flex items-center"> <p className="transition transform duration-500 ease-in-out group-hover:text-red-600 mx-1">Mağazam</p> <ShoppingBasket className="transition  transform duration-500 ease-in-out h-10 w-20 rounded-lg text-blue-600 group-hover:text-red-600 " fontSize="small"/></div>
              <div className="cursor-pointer"   onClick={() => { setProfileBar(!profileBar) }}><Avatar alt={user?.result.username} src={PF + "avatar.png"} />  </div></div>}
            {profileBar && (
                <ul className="p-2 w-40 border-r bg-white absolute rounded right-0 shadow mt-16 top-0 ">
                    <li onClick={() => history.push(`/profile/${user.result.username}`)} className="cursor-pointer text-gray-600 text-sm leading-3 tracking-normal py-2 hover:text-gray-300 focus:text-gray-300 focus:outline-none">
                        <div className="flex items-center">
                            <svg xmlns="http://www.w3.org/2000/svg" className="icon icon-tabler icon-tabler-user" width={20} height={20} viewBox="0 0 24 24" strokeWidth="1.5" stroke="currentColor" fill="none" strokeLinecap="round" strokeLinejoin="round">
                                <path stroke="none" d="M0 0h24v24H0z" />
                                <circle cx={12} cy={7} r={4} />
                                <path d="M6 21v-2a4 4 0 0 1 4 -4h4a4 4 0 0 1 4 4v2" />
                            </svg>
                            <span className="ml-2">Profilim</span>
                        </div>
                    </li>
                    <li onClick={() => { logout(); setProfileBar(!profileBar) }} className="cursor-pointer text-gray-600 text-sm leading-3 tracking-normal mt-2 py-2 hover:text-gray-300 flex items-center focus:text-gray-300 focus:outline-none">
                        <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" viewBox="0 0 20 20" fill="currentColor">
                            <path fillRule="evenodd" d="M3 3a1 1 0 00-1 1v12a1 1 0 102 0V4a1 1 0 00-1-1zm10.293 9.293a1 1 0 001.414 1.414l3-3a1 1 0 000-1.414l-3-3a1 1 0 10-1.414 1.414L14.586 9H7a1 1 0 100 2h7.586l-1.293 1.293z" clipRule="evenodd" />
                        </svg>
                        <span className="ml-2">Çıkış Yap</span>
                    </li>
                </ul>
            )}
        </div>
    )
}

export default Header
