import React, { useState } from 'react'

function Login() {
    const [user] = useState(JSON.parse(localStorage.getItem('profile')));
    const [LogOrSign , setLogOrSign] = useState(false)
    
    return (
        <div className="items-center mt-16 w-3/4 h-3/4 rounded-xl bg-blue-700 flex flex-col">
        {user ? <div className="m-7 h-full flex flex-col justify-between items-center">
           <h2>Giriş Yap</h2>
            <div className="flex flex-col h-2/4 items-center justify-around">

               <div className="flex bg-blue-50 rounded-lg w-full items-center "><input className="outline-none p-3 rounded-lg" placeholder="E-posta" type="text"></input></div> 
               <div className="flex bg-blue-50 rounded-lg w-full items-center "> <input className="outline-none p-3 rounded-lg" placeholder="Şifre" type="password"></input></div>
               
            </div>
            <button className="w-3/4 h-10 rounded-xl cursor-pointer bg-green-400">Giriş Yap</button>

        </div> : <div> User yok </div>}
       
    </div>
    )
}

export default Login
