import {useEffect, useState} from 'react';
import ReactMapGL, { Marker , Popup} from 'react-map-gl';
import {Room} from "@material-ui/icons";

function Map({pins,dimension}) {
    const [showPopup, togglePopup] = useState(false);
    const [viewport, setViewport] = useState({
      width: dimension.width + "px",
      height:dimension.height + "px" ,
      latitude: 41,
      longitude: 28,
      zoom: 8
    });

    useEffect(()=>{
        
    },[dimension])

    return (
        <div>
        <ReactMapGL {...viewport}
          mapboxApiAccessToken={process.env.REACT_APP_MAPBOX}
          onViewportChange={(viewport) => setViewport(viewport)}
          mapStyle="mapbox://styles/semiharslanhw/cku9yduu72ch917mwngkd5uwo"
          >
          <Marker latitude={42} longitude={28} offsetLeft={-20} offsetTop={-10}>
          
           <Room style={{fontSize:viewport.zoom * 8}}></Room>
            
          </Marker>
          <Popup
            latitude={41}
            longitude={28}
            closeButton={true}
            closeOnClick={false}
            anchor="left" >
            <div className="w-60 h-50 flex flex-col justify-between">
              <label className="border-b-2 border-red-500 ">Place</label>
              <h4>İstanbul</h4>
              <label className="border-b-2 border-red-500 ">Company</label>
              <p>LC Waikiki</p>
              <label className="border-b-2 border-red-500 ">Category</label>
              <p>Kıyafet</p>
              <a href="/" className="border-b-2 border-red-500 hover:bg-blue-600 rounded-sm hover:text-white ">Go Inside</a>
              </div>
          </Popup>
        </ReactMapGL>
  
      </div>
    )
}

export default Map
