import React, { useEffect, useRef, useState } from 'react'
import Map from './map'

function HomeBody() {
    const [dimension, setDimension] = useState([{
        "width":document.getElementById("maping").clientWidth,
        "height":document.getElementById("maping").clientHeight
    }]);
    
    
    useEffect(() => {
             
        window.addEventListener("resize", getDimension)
        
    },[])
    const getDimension = () => {
        const dim = [{
            "width":document.getElementById("maping").clientWidth,
            "height":document.getElementById("maping").clientHeight
        }]
        setDimension(dim)
    }
    return (
        <div className=" flex items-center justify-around h-3/4 w-3/4">

            <div id="maping" className=" w-2/4 h-full bg-red-400 "></div>
            

            <div className="w-2/4 h-full bg-yellow-50">
                <Map dimension={dimension} ></Map>
            </div>

        </div>
    )
}

export default HomeBody
