import React from 'react'
import Slide from "react-slick"


function Slider() {
    const urunler = [
      {
        acıklama:"Dışarı çıkıp gezmek , alışveriş yapmak istiyosun ama aynı zamanda zamanının boşa gitmesini istemiyorsan doğru yerdesin "
        , img:"https://2.bp.blogspot.com/_ZmuJjitjb2E/TIn1-pLAy4I/AAAAAAAAB6Q/N5_D0ZfhvOY/s1600/TooManyChoices.jpg"
      },
      {
        acıklama:"Final Proje sizin için binlerce marketin stoğunu öne çıkarır ve sizi market market dolaşıp istediğiniz ürünü arama sorunundan kurtarır",
        img:"https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcT_gdj7_Q025nQZMro1lhMGjdkcIkxkFhn3oQ&usqp=CAU"
      },
      
    ]
    const settings = {
        dots: true,
        pauseOnFocus:true,
        infinite: true,
        arrows:false,
        speed: 500,
        slidesToShow: 2,
        autoplay:true,
        slidesToScroll: 1
      };
    return (
  <div className="h-screen w-full flex flex-col items-center">
  <div className="container mx-auto  text-white items-center justify-items-center">
        
        <Slide {...settings}>
       
       {urunler.map((data)=>{
          <div className=" p-3 mx-auto shadow-sm w-60 h-60 ">
            <div className="text-left font-sans text-black container mx-auto w-full h-full bg-white flex">
              <div className="m-5 w-1/2 bg-red-600">
                <img className="shadow-lg object-cover w-full h-full" src={data.img}></img>
                
              </div>
              <div className="m-5 w-1/2 ">
                <p className="">{data.acıklama}</p>
              </div>
            </div>
          </div>
         
       })}  
        </Slide>
      </div>
      </div>
    )
}

export default Slider
