import axios from 'axios';
import React, { useState } from 'react'
import { useHistory } from "react-router";

function Login() {
   const [user] = useState(JSON.parse(localStorage.getItem('profile')));
   const [loginEmail, setLoginEposta] = useState();
   const [LoginPassword, setLoginPassword] = useState();
   const [signUpEmail, setSignUpEmail] = useState();
   const [signUpPassword, setSignUpPassword] = useState();
   const [repeatSignUpPassword, setRepeatSignUpPassword] = useState();
   const [LogOrSign, setLogOrSign] = useState(false)
   const history = useHistory();


   const handleSubmit = async (e) => {
      e.preventDefault();
      if (signUpEmail === ""  || signUpPassword === "" || repeatSignUpPassword === "") {
          console.log("Lütfen Boş Bırakmayın")
      } else {
          if (signUpPassword === repeatSignUpPassword) {
              try {
                  await axios.post("http://localhost:8800/auth/register", { signUpEmail, signUpPassword });
                 alert(`${signUpEmail}, Hoşgeldin.`)
                  setTimeout(() => {
                      history.push('/login')
                    }, 1000)
              } catch (error) {
                  console.log("Kullanıcı zaten mevcut")
              }
          } else {
              console.log('Şifreler Uyuşmuyor');
          }
      }
  
  }
   const signUp = () => {
      if (repeatSignUpPassword === signUpPassword) {
         console.log(repeatSignUpPassword, signUpPassword, signUpEmail)

      }
      else {
         alert("Şifreler eşleşmiyor");
      }

   }
   return (
      <div className="text-black h-full w-full mt-20  flex flex-col items-center justify-center">
             <h6 className="font-mono font-bold text-2xl">HOŞGELDİN , </h6>
             <h1>İndirimleri ve fırsatları kaçırmamak için kayıt ol veya giriş yap</h1>
         {<div className="items-center w-2/5 h-3/4 m-0  bg-gray-300 rounded-2xl flex flex-col">
            {LogOrSign ? <div className="m-7 h-full flex flex-col justify-between items-center">
               <h2>Kayıt Ol</h2>
               <div className="flex flex-col h-2/4 items-center justify-around">

                  <div className="flex bg-blue-50 rounded-lg w-full items-center "><input className="outline-none p-3 rounded-lg text-black" placeholder="E-posta" value={signUpEmail} onChange={(m) => { setSignUpEmail(m.target.value) }} type="email"></input></div>
                  <div className="flex bg-blue-50 rounded-lg w-full items-center "> <input className="outline-none p-3 rounded-lg text-black" placeholder="Şifre" value={signUpPassword} onChange={(m) => { setSignUpPassword(m.target.value) }} type="password"></input></div>
                  <div className="flex bg-blue-50 rounded-lg w-full items-center "> <input className="outline-none p-3 rounded-lg text-black" placeholder="Şifre Tekrar" value={repeatSignUpPassword} onChange={(m) => { setRepeatSignUpPassword(m.target.value) }} type="password"></input></div>

               </div>
               <button className="w-3/4 h-10 rounded-xl cursor-pointer bg-green-400" onClick={(e)=>{handleSubmit(e)}}>Kayıt Ol</button>
               <p className="text-black">Hesabın Varsa (<span className="text-green-700 cursor-pointer hover:text-white" onClick={() => {
                  setRepeatSignUpPassword("");
                  setSignUpPassword("");
                  setSignUpEmail(""); setLogOrSign(false)
                  history.push("/giris")
               }}>Giriş Yap</span>)</p>
            </div> :

               <div className="m-7 h-full flex flex-col justify-between items-center">
                  <h2>Giriş Yap</h2>
                  <div className="flex flex-col h-2/6 items-center justify-around">

                     <div className="flex text-black bg-blue-50 rounded-lg w-full items-center "><input className="outline-none p-3 rounded-lg" placeholder="E-posta" type="text"></input></div>
                     <div className="flex text-black bg-blue-50 rounded-lg w-full items-center "> <input className="outline-none p-3 rounded-lg" placeholder="Şifre" type="password"></input></div>

                  </div>
                  <button className="w-3/4 h-10 rounded-xl cursor-pointer bg-green-400">Giriş Yap</button>
                  <p className="text-black">Hesabın Yoksa (<span className="text-green-700 cursor-pointer hover:text-white" onClick={() => {
                     setRepeatSignUpPassword("");
                     setSignUpPassword("");
                     setSignUpEmail(""); 
                     setLogOrSign(true);
                     history.push("/kayıt")
                  }}>Hesap Aç</span>)</p>
               </div>}
         </div>}
      </div>
   )
}

export default Login
